import React, { useState } from "react";
import "./styles.css";
import Settings from './Settings.js'

const Tabs = props => {
    const [activeTabIndex, setActiveTabIndex] = useState(0);
    const activeTab = props.children[activeTabIndex];
    return (
        <div>
            <div className="tabs">
                {props.children.map((tab, i) => (
                    <button
                        className="tab-btn"
                        onClick={() => {
                            setActiveTabIndex(i);
                        }}
                        key={i}
                    >
                        {tab.props.title}
                    </button>
                ))}
            </div>
            <div className="tab-indicator-container">

            </div>
            <div className="tab-content">{activeTab.props.children}</div>
        </div>
    );
};

export default function App() {
    return (
        <div className="App">
            <h1>Tab Component Demo</h1>
            <h2>Start adding tabs to see some magic happen!</h2>

            <div className="dashboard">
                <div className="innerdash">
                    <button onClick={() => {
                        setActiveTabIndex(i);
                    }}
                        key={i}><p>Contacts</p></button>
                    <button onClick={() => {
                        setActiveTabIndex(i);
                    }}
                        key={i}><p>Add new contact</p></button>
                </div>
                <div className="innerdash">
                    <button onClick={() => {
                        setActiveTabIndex(i);
                    }}
                        key={i}>Sync with MemoryGlass</button>
                    <button onClick={() => {
                        setActiveTabIndex(i);
                    }}
                        key={i}>Settings</button>
                </div>
            </div>

        </div>
    );
}
